import Logo from "../../assets/logo.png";
import "./login.styles.scss";
import { Form, Button } from "react-bootstrap";
import React,{useRef} from 'react';
import axios from 'axios';
import { withRouter } from "react-router-dom";

const Login = ({history}) => {
  const usernameRef=useRef(null)
  const passwordRef=useRef(null)
  const onClickFunction=()=>{
      axios
      .post("http://localhost:3001/login",{
        username:usernameRef.current.value,
        password:passwordRef.current.value
      })
      .then((res) => {
        console.log(res.data);
        if(res.data.status==="Success")
        {
          history.push(`/quiz`)
        }

      })
      .catch(function (error) {
        console.log("an error has occurred : ", error);
      })

  }

  return (
    // <div className="login">
    //   <div>
    //     <img src={Logo} alt='logo' width='50%'/>
    //     <h1>
    //       Sign in
    //     </h1>
    //     <div className="login-form">
    //       <form>
    //         <div>
    //           <input className="input-username" type="text" placeholder="Username" required={true} />
    //         </div>
    //         <div>
    //           <input className="input-password" type="password" placeholder="Password"  required={true}/>
    //         </div>
    //         <button type="submit" className="btn btn-dark submit-btn">SIGN IN</button>
    //       </form>
    //     </div>
    //   </div>
    // </div>

    <div className="login">

      <img src={Logo} width="200px" alt="Xenia logo"/>
      {/* <h2>Login</h2> */}
      <Form>
        <Form.Group controlId="formBasicEmail">
          <Form.Control ref={usernameRef} type="email" placeholder="Enter email" />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Control ref={passwordRef} type="password" placeholder="Password" />
        </Form.Group>
        <Button variant="dark" size='md' type="submit" onClick={()=>onClickFunction()}>
          Login
        </Button>
      </Form>
    </div>
  );
};

export default withRouter(Login);
