import React from 'react';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import './answer.styles.scss';


export default function RadioButtonsGroup({answers}) {
  const [value, setValue] = React.useState('female');

  const handleChange = (event) => {
    setValue(event.target.value);
  };

  return (
    <FormControl component="fieldset">

      <RadioGroup aria-label="gender" name="gender1" value={value} onChange={handleChange}>
        <FormControlLabel value="opt1" control={<Radio />} label={answers[0].text}/>
        <FormControlLabel value="opt2" control={<Radio />} label={answers[1].text}/>
        <FormControlLabel value="opt3" control={<Radio />} label={answers[2].text} />
        <FormControlLabel value="opt4" control={<Radio />} label={answers[3].text} />

      </RadioGroup>
    </FormControl>
  );
}
