import React, { Component } from "react";
import axios from "axios";
import styled from "styled-components";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import {
  setFetchedQuestionsToState,
  selectedQuestionNext,
  selectedQuestionPrevious,
} from "../../redux/question/question.actions";
import {
  getSelectedQuestion,
  getSelectedQuestionNumber,
} from "../../redux/question/question.selector";

import "./quiz-page.styles.scss";

//import GridComponent from "../../components/grid/grid.component";
import McqComponent from "../../components/mcq/mcq.component";
import TimerComponent from "../../components/timer/timer.component";

import TemporaryDrawer from "../../components/drawer/TemporaryDrawer";

import Button from "@material-ui/core/Button";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import Typography from "@material-ui/core/Typography";
import //Typography,
//AppBar,
// Card,
// CardActions,
// CardContent,
// CardMedia,
//CssBaseline,
// Grid,
//Toolbar,
// Container,
//ButtonGroup,
"@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

class QuizPage extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  componentDidMount() {
    axios
      .post("http://localhost:3001/fetchQuestions")
      .then((res) => {
        console.log(res);
        this.setState(res.data);
        this.props.setFetchedQuestionsToState(res.data);
        console.log("action initialised");
      })
      .catch(function (error) {
        console.log("an error has occurred : ", error);
      });
  }

  render() {
    return (
      <Wrapper>
        <div id="paddingStyle" className="container-fluid">
          <div className="overlay">
            <Typography className="heading" variant="h2">
              XENIA
            </Typography>
            <Typography className="heading">Event Name</Typography>
            <TemporaryDrawer />
            {/* <div id="timerRow" className="row">
              {/* Instructions */}
            {/* <div className="col-lg-10 col-md-9 col-sm-2 div1"></div> */}
            {/* <div className="col-lg-2 col-md-2 col-sm-10"> */}
            {/* </div> */}
            {/* </div> */}
            <TimerComponent />
            <div className="row">
              {/* Instructions */}
              <div className="col-lg-3 col-md-2 col-sm-12 div1"></div>

              <div className="col-lg-7 col-md-10 col-sm-1">
                <McqComponent />
              </div>
            </div>
            <div className="row row2">
              <div className="col-lg-3 col-md-3 col-sm-11 "></div>
              <div className="col-lg-7 col-md-7 col-sm-1">
                <Wrapper>
                  <div className="row row1">
                    <ButtonGroup>
                      <Button
                        variant="contained"
                        color="secondary"
                        className="buttonStyle"
                        onClick={() =>{
                            this.props.selectedQuestionPrevious(
                              this.props.selectedQuestionNumber
                            )
                            console.log(this.props);
                          }
                        }
                      >
                        Previous
                      </Button>
                      <Button
                        variant="contained"
                        color="secondary"
                        className="buttonStyle"
                        onClick={()=>this.props.selectedQuestionNext(
                          this.props.selectedQuestionNumber
                        )}
                      >
                        Submit
                      </Button>
                      <Button
                        variant="contained"
                        color="secondary"
                        className="buttonStyle"
                      >
                        Next
                      </Button>
                    </ButtonGroup>
                  </div>
                </Wrapper>
              </div>
            </div>
          </div>
        </div>
      </Wrapper>
    );
  }
}

const Wrapper = styled.section`
  html {
    overflow: hidden;
  }
  .buttonStyle {

    margin-right: 10%;
    background: #f11794;
    border-radius:0;
    &:hover {
      background: black;
      border: 2px solid #f11794;
      color: #f11794;
      box-shadow: 1px 1px 2px black, 0 0 45px #f11794, 0 0 5px darkblue;
    }
  }
  .container-fluid {
    padding-right: 0;
    padding-left: 0;
  }
  .row1 {
    display: revert;
  }
  .row2{
    margin-top:2%;
  }
  .row {
    dispplay: revert;
  }
  #timerRow {
    margin-top:-12%:
    margin-right: 2%;

  }
`;
const mapDispatchToProps = (dispatch) => ({
  setFetchedQuestionsToState: (questions) =>
    dispatch(setFetchedQuestionsToState(questions)),
  selectedQuestionNext: (num) => dispatch(selectedQuestionNext(num)),
  selectedQuestionPrevious: (num) => dispatch(selectedQuestionPrevious(num)),
});

const mapStateToProps = createStructuredSelector({
  selectedQuestion: getSelectedQuestion,
  selectedQuestionNumber: getSelectedQuestionNumber,
});

export default connect(mapStateToProps, mapDispatchToProps)(QuizPage);
