import React from "react";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
// import Typography from "@material-ui/core/Typography";
// import FormLabel from "@material-ui/core/FormLabel";
import "./answer.styles.scss";

import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import styled from "styled-components";
import {
  getSelectedQuestionAnswers,
  getSelectedQuestion,
} from "../../redux/question/question.selector";
import { setSelectedAnswer } from "../../redux/question/question.actions";
import cyan from "@material-ui/core/colors/cyan";

const StyledRadio = styled(Radio)`
  color: ${cyan[400]};
  &.Mui-checked {
    color: ${cyan[600]};
  }
`;
const RadioButtonsGroup = function ({
  answers,
  selectedQuestion,
  setSelectedAnswer,
}) {
  const selectedAnswer = selectedQuestion.selectedAnswer;
  const [value, setValue] = React.useState(selectedAnswer);

  // React.useEffect(()=>{
  //   setValue(selectedAnswer)
  // },[selectedAnswer])

  const handleChange = (event) => {
    setValue(event.target.value);
    setSelectedAnswer(event.target.value);
  };
  //{answers[0].text}
  //{answers[1].text}
  //{answers[2].text}
  //{answers[3].text}
  return (
    <Wrapper>
      <FormControl component="fieldset" className="styling vertical-center-row">
        <RadioGroup
          aria-label="options"
          name="gender1"
          value={value}
          onChange={handleChange}
        >
          {/* <<<<<<< HEAD */}
          {/* <FormControlLabel
          value="1"
          control={<Radio />}
          label={
            <span style={{ width: 40 }}>
              Hello Randrom Option Hello Randrom Option Hello Randrom Option
              Hello Randrom Option Hello Randrom Option{" "}
            </span>
          }
        />

        <FormControlLabel value="2" control={<Radio />} label="Random Option" />
        <FormControlLabel value="3" control={<Radio />} label="Random Option" />
        <FormControlLabel value="4" control={<Radio />} label="Random Option" /> */}
          {/* ======= */}
          <FormControlLabel
            value="0"
            control={<StyledRadio />}
            label={answers[0].text}
            className="FormControlLabelStyling"
          />
          <FormControlLabel
            value="1"
            control={<StyledRadio />}
            label={answers[1].text}
          />
          <FormControlLabel
            value="2"
            control={<StyledRadio />}
            label={answers[2].text}
          />
          <FormControlLabel
            value="3"
            control={<StyledRadio />}
            label={answers[3].text}
          />
        </RadioGroup>
      </FormControl>
    </Wrapper>
  );
};

const mapStateToProps = createStructuredSelector({
  answers: getSelectedQuestionAnswers,
  selectedQuestion: getSelectedQuestion,
});

const mapDispatchToProps = (dispatch) => ({
  setSelectedAnswer: (num) => dispatch(setSelectedAnswer(num)),
});
const Wrapper = styled.section`
  @media only screen and (min-width: 600px) {
    .MuiFormControlLabel-label {
      font-size: 20px;
    }
  }
`;
export default connect(mapStateToProps, mapDispatchToProps)(RadioButtonsGroup);
