import React, { Component } from "react";
import axios from "axios";

import {connect} from 'react-redux';
import {createStructuredSelector} from 'reselect';
import {setFetchedQuestionsToState,selectedQuestionNext,selectedQuestionPrevious} from '../../redux/question/question.actions';
import { getSelectedQuestion,getSelectedQuestionNumber } from '../../redux/question/question.selector';

import "./quiz-page.styles.scss";

import GridComponent from "../../components/grid/grid.component";
import McqComponent from "../../components/mcq/mcq.component";
import TimerComponent from "../../components/timer/timer.component";
import Button from "../../components/button/button.component";
import {
  //Typography,
  //AppBar,
  // Card,
  // CardActions,
  // CardContent,
  // CardMedia,
  //CssBaseline,
  // Grid,
  //Toolbar,
  // Container,
  //ButtonGroup,
} from "@material-ui/core";

class QuizPage extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  componentDidMount(){
    axios
    .post("http://localhost:3001/fetchQuestions")
    .then((res)=> {
      console.log(res);
      this.setState(res.data);
      this.props.setFetchedQuestionsToState(res.data);
      console.log("action initialised");
    })
    .catch(function (error) {
      console.log("an error has occurred : ", error);
    });
  }
  render() {
    return (
      <div className="container-fluid ">
      <div className="overlay">
        <div className="row">
          {/* Instructions */}
          <div className="col-lg-3 col-md-5 col-sm-12 div1">
            <GridComponent />
          </div>

          {/* Signin */}



          <div className="col-lg-9 col-md-7 col-sm-1">
            <TimerComponent />
            <McqComponent question={this.props.selectedQuestion.content} answers={this.props.selectedQuestion.answers}/>
            <div className="row">
              <Button
                className="button-row"
                text="Previous"
                type="btn btn-primary"
                onClickFunction={selectedQuestionPrevious(this.props.selectedQuestionNumber)}
              >Previous</Button>
              <Button
                className="button-row"
                text="Next"
                type="btn btn-primary"
                onClickFunction={selectedQuestionNext(this.props.selectedQuestionNumber)}
              >Next</Button>
              <Button
                className="button-row"
                text="Submit"
                type="btn btn-primary"
              >Submit</Button>



            </div>
          </div>
        </div>
        </div>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  setFetchedQuestionsToState: (questions) =>
    dispatch(setFetchedQuestionsToState(questions)),
  selectedQuestionNext:(num)=>
    dispatch(selectedQuestionNext(num)),
  selectedQuestionPrevious:(num)=>
    dispatch(selectedQuestionPrevious(num))

});

const mapStateToProps=createStructuredSelector({
    selectedQuestion:getSelectedQuestion,
    selecetedQuestionNumber:getSelectedQuestionNumber

})

export default connect(mapStateToProps,mapDispatchToProps)(QuizPage);
