import React from "react";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";

import "./question.styles.scss";
import { colors } from "@material-ui/core";

import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { getSelectedQuestion } from "../../redux/question/question.selector";

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(4),
      width: "100%",
      display: "flex",
      flexWrap: "wrap",
    },
  },
}));

const Question = function ({ question }) {
  const classes = useStyles();
  const [value, setValue] = React.useState("Controlled");

  const handleChange = (event) => {
    setValue(event.target.value);
  };
  return (
    <form className={classes.root} noValidate autoComplete="off">
      <div className="question" style={{ justifyItems: "left" }}>
        <TextField
          id="standard-multiline-flexible"
          label="Question"
          multiline
          rowsMax={4}
          // value="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna  "
          value={question.content}
          onChange={handleChange}
          InputProps={{
            style: { fontSize: 25 },
            readOnly: true,
            disableUnderline: true,
          }}
        />
        {/* <TextField
          id="standard-multiline-flexible"
          label="Question"
          // <<<<<<< HEAD
          value="Random Question Random Question Random Question Random Question Random Question Random Question Random Question Random Question Random QuestionRandom QuestionRandom QuestionRandom QuestionRandom QuestionRandom QuestionRandom QuestionRandom QuestionRandom QuestionRandom QuestionRandom QuestionRandom QuestionRandom Question" //{question.content}
          // =======
          //           value={question.content}          //{question.content}
          // >>>>>>> 37a20a9e73339f19da59bac39c0b051dd5689f58
          InputProps={{
            style: { fontSize: 25 },
            readOnly: true,
            disableUnderline: true,
          }}
        /> */}
      </div>
    </form>
  );
};

const mapStateToProps = createStructuredSelector({
  question: getSelectedQuestion,
});

export default connect(mapStateToProps, null)(Question);
